import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [deviceId, setDeviceId] = useState('');

  // Generate a random device ID when component mounts
  useEffect(() => {
    const newDeviceId = 'device_' + Math.random().toString(36).substring(2, 15);
    setDeviceId(newDeviceId);
    
    // Get initial options from the backend
    fetchInitialOptions(newDeviceId);
  }, []);

  const fetchInitialOptions = async (id) => {
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:3001/api/chat', {
        deviceId: id,
        message: 'start'
      });
      
      setMessages([
        { text: response.data.response, sender: 'bot' }
      ]);
    } catch (error) {
      console.error('Error fetching options:', error);
      setMessages([
        { text: 'Welcome to Our Restaurant Chatbot! Please select an option:', sender: 'bot' },
        { text: '1 - Place an Order (or add items if order is active)\n99 - Checkout Order\n98 - See Order History\n97 - See Current Order\n0 - Cancel Order\n96 - Schedule Order (optional)', sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (inputText.trim() === '') return;

    // Add user message
    const newMessages = [...messages, { text: inputText, sender: 'user' }];
    setMessages(newMessages);
    setInputText('');
    setIsLoading(true);

    try {
      // Send message to backend
      const response = await axios.post('http://localhost:3001/api/chat', {
        deviceId: deviceId,
        message: inputText
      });

      // Add bot response
      setMessages([...newMessages, { text: response.data.response, sender: 'bot' }]);
    } catch (error) {
      console.error('Error sending message:', error);
      // Add error message
      setMessages([...newMessages, { 
        text: 'Sorry, there was an error processing your request. Please try again.', 
        sender: 'bot' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chatbot-container" style={{ 
      maxWidth: '600px', 
      margin: '0 auto', 
      border: '1px solid #ddd', 
      borderRadius: '8px',
      overflow: 'hidden'
    }}>
      <div className="chat-messages" style={{ 
        height: '400px', 
        overflowY: 'auto', 
        padding: '20px',
        backgroundColor: '#f9f9f9'
      }}>
        {messages.map((message, index) => (
          <div 
            key={index} 
            style={{ 
              marginBottom: '10px',
              textAlign: message.sender === 'user' ? 'right' : 'left'
            }}
          >
            <div style={{ 
              display: 'inline-block',
              padding: '10px 15px',
              borderRadius: '18px',
              backgroundColor: message.sender === 'user' ? '#007bff' : '#e9ecef',
              color: message.sender === 'user' ? 'white' : 'black',
              maxWidth: '70%',
              whiteSpace: 'pre-line'
            }}>
              {message.text}
            </div>
          </div>
        ))}
        {isLoading && (
          <div style={{ textAlign: 'left', marginBottom: '10px' }}>
            <div style={{ 
              display: 'inline-block',
              padding: '10px 15px',
              borderRadius: '18px',
              backgroundColor: '#e9ecef',
              color: 'black',
              maxWidth: '70%'
            }}>
              Typing...
            </div>
          </div>
        )}
      </div>
      <form onSubmit={handleSendMessage} style={{ 
        display: 'flex', 
        padding: '10px',
        borderTop: '1px solid #ddd'
      }}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Type your message..."
          style={{ 
            flex: 1, 
            padding: '10px', 
            borderRadius: '4px',
            border: '1px solid #ddd',
            marginRight: '10px'
          }}
        />
        <button 
          type="submit"
          style={{ 
            padding: '10px 20px', 
            backgroundColor: '#007bff', 
            color: 'white', 
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Send
        </button>
      </form>
    </div>
  );
}

export default ChatBot;
