import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [deviceId, setDeviceId] = useState('');
  const [connectionError, setConnectionError] = useState(false);
  const [currentOrderId, setCurrentOrderId] = useState(null);
  const [currentOrderAmount, setCurrentOrderAmount] = useState(0);
  const navigate = useNavigate();
  const location = useLocation();

  // Generate a random device ID when component mounts
  useEffect(() => {
    const newDeviceId = 'device_' + Math.random().toString(36).substring(2, 15);
    setDeviceId(newDeviceId);

    // Get initial options from the backend
    fetchInitialOptions(newDeviceId);
  }, []);

  // Check for payment status in URL parameters
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const paymentStatus = params.get('payment');
    const orderId = params.get('orderId');

    if (paymentStatus && orderId) {
      let message = '';

      switch (paymentStatus) {
        case 'success':
          message = 'Payment successful! Your order has been confirmed. Thank you for your purchase!';
          break;
        case 'cancelled':
          message = 'Payment was cancelled. Would you like to try again?\n\n1 - Try payment again\n0 - Cancel payment';
          break;
        case 'failed':
          message = 'Payment failed. Please try again or contact support.\n\n1 - Try payment again\n0 - Cancel payment';
          break;
        case 'error':
          message = 'There was an error processing your payment. Please try again or contact support.\n\n1 - Try payment again\n0 - Cancel payment';
          break;
        default:
          return;
      }

      setMessages(prev => [...prev, { text: message, sender: 'bot' }]);
    }
  }, [location]);

  const fetchInitialOptions = async (id) => {
    setIsLoading(true);
    setConnectionError(false);
    try {
      const response = await axios.post('http://localhost:3001/api/chat', {
        deviceId: id,
        message: 'start'
      });

      setMessages([
        { text: response.data.response, sender: 'bot' }
      ]);
    } catch (error) {
      console.error('Error fetching options:', error);
      setConnectionError(true);
      setMessages([
        { text: 'Welcome to Our Restaurant Chatbot! Please select an option:', sender: 'bot' },
        { text: '1 - Place an Order (or add items if order is active)\n99 - Checkout Order\n98 - See Order History\n97 - See Current Order\n0 - Cancel Order\n96 - Schedule Order (optional)', sender: 'bot' },
        { text: '⚠️ Note: Backend connection failed. This is a demo mode with simulated responses.', sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handlePayment = (orderId, amount) => {
    // Redirect to payment page
    navigate(`/payment?orderId=${orderId}&amount=${amount}&deviceId=${deviceId}`);
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (inputText.trim() === '') {
      return;
    }

    // Special command to trigger payment
    if (inputText.trim().toUpperCase() === 'PAY') {
      // Find the last message with order details
      const lastBotMessage = messages.filter(m => m.sender === 'bot').pop();
      const orderIdMatch = lastBotMessage?.text.match(/Order #(\d+)/);
      const amountMatch = lastBotMessage?.text.match(/Total: \$(\d+(\.\d{2})?)/);

      if (orderIdMatch && orderIdMatch[1] && amountMatch && amountMatch[1]) {
        const orderId = orderIdMatch[1];
        const amount = parseFloat(amountMatch[1]);
        setCurrentOrderId(orderId);
        setCurrentOrderAmount(amount);
        handlePayment(orderId, amount);

        // Add user message and bot response
        setMessages(prev => [
          ...prev, 
          { text: inputText, sender: 'user' },
          { text: `Initiating payment for Order #${orderId} - $${amount.toFixed(2)}...`, sender: 'bot' }
        ]);
      } else {
        // Add user message and error response
        setMessages(prev => [
          ...prev, 
          { text: inputText, sender: 'user' },
          { text: 'No active order found to pay for. Please place an order first.', sender: 'bot' }
        ]);
      }

      setInputText('');
      return;
    }

    // Add user message
    const newMessages = [...messages, { text: inputText, sender: 'user' }];
    setMessages(newMessages);
    setInputText('');
    setIsLoading(true);
    setConnectionError(false);

    try {
      // Send message to backend
      const response = await axios.post('http://localhost:3001/api/chat', {
        deviceId: deviceId,
        message: inputText
      });

      // Add bot response
      setMessages([...newMessages, { text: response.data.response, sender: 'bot' }]);

      // Check if this is a payment selection (after checkout)
      // Look for the last bot message that contains payment options
      const lastBotMessage = messages.filter(m => m.sender === 'bot').pop();
      console.log("Last bot message:", lastBotMessage);

      const isPaymentPrompt = lastBotMessage && 
        (lastBotMessage.text.includes('Pay with Paystack') || 
         lastBotMessage.text.includes('Would you like to pay now'));

      console.log("Is payment prompt:", isPaymentPrompt);
      console.log("User input:", inputText.trim());

      if (inputText.trim() === '1' && isPaymentPrompt) {
        console.log("Attempting to process payment...");

        // Extract order ID and amount from the response
        const orderIdMatch = response.data.response.match(/Order #(\d+)/);
        const amountMatch = response.data.response.match(/Total: \$(\d+(\.\d{2})?)/);

        console.log("Order ID match:", orderIdMatch);
        console.log("Amount match:", amountMatch);

        if (orderIdMatch && orderIdMatch[1] && amountMatch && amountMatch[1]) {
          const orderId = orderIdMatch[1];
          const amount = parseFloat(amountMatch[1]);
          console.log("Extracted order ID:", orderId);
          console.log("Extracted amount:", amount);

          setCurrentOrderId(orderId);
          setCurrentOrderAmount(amount);
          handlePayment(orderId, amount);
        } else {
          // Try to extract from the last bot message if not in current response
          console.log("Trying to extract from last bot message...");
          const lastOrderIdMatch = lastBotMessage.text.match(/Order #(\d+)/);
          const lastAmountMatch = lastBotMessage.text.match(/Total: \$(\d+(\.\d{2})?)/);

          console.log("Last message Order ID match:", lastOrderIdMatch);
          console.log("Last message Amount match:", lastAmountMatch);

          if (lastOrderIdMatch && lastOrderIdMatch[1] && lastAmountMatch && lastAmountMatch[1]) {
            const orderId = lastOrderIdMatch[1];
            const amount = parseFloat(lastAmountMatch[1]);
            console.log("Extracted order ID from last message:", orderId);
            console.log("Extracted amount from last message:", amount);

            setCurrentOrderId(orderId);
            setCurrentOrderAmount(amount);
            handlePayment(orderId, amount);
          } else {
            console.log("Failed to extract order details");
            setMessages(prev => [...prev, { 
              text: 'Sorry, there was an error processing your payment. Please try again.', 
              sender: 'bot' 
            }]);
          }
        }
      }
    } catch (error) {
      console.error('Error sending message:', error);
      setConnectionError(true);

      // Simulate a response for demo purposes
      let simulatedResponse = '';

      switch(inputText.trim()) {
        case '1':
          // Check if we're in payment mode
          const lastBotMessage = messages.filter(m => m.sender === 'bot').pop();
          const isPaymentPrompt = lastBotMessage && 
            (lastBotMessage.text.includes('Pay with Paystack') || 
             lastBotMessage.text.includes('Would you like to pay now'));

          if (isPaymentPrompt) {
            simulatedResponse = 'Order #12345 placed successfully! Total: $25.00\n\nWould you like to pay now?\n\n1 - Pay with Paystack\n0 - Cancel payment';
          } else {
            simulatedResponse = 'Great! What would you like to order?\n\nAvailable items:\n1. Pizza - $10\n2. Burger - $8\n3. Salad - $6\n4. Pasta - $9\n\nPlease enter the number of the item you want to order.';
          }
          break;
        case '99':
          simulatedResponse = 'Order #12345 placed successfully! Total: $25.00\n\nWould you like to pay now?\n\n1 - Pay with Paystack\n0 - Cancel payment\n\nOr would you like to place a new order?\n\n2 - Place a new order';
          break;
        case '98':
          simulatedResponse = 'Your order history:\n\nNo previous orders found.';
          break;
        case '97':
          simulatedResponse = 'Your current order:\n\nNo active order.';
          break;
        case '0':
          simulatedResponse = 'Your order has been cancelled.';
          break;
        case '96':
          simulatedResponse = 'Please enter the date and time you would like to schedule your order for (e.g., "2023-12-25 18:00").';
          break;
        default:
          simulatedResponse = 'I didn\'t understand that. Please select a valid option:\n\n1 - Place an Order (or add items if order is active)\n99 - Checkout Order\n98 - See Order History\n97 - See Current Order\n0 - Cancel Order\n96 - Schedule Order (optional)';
      }

      // Add simulated response
      setMessages([...newMessages, { 
        text: simulatedResponse + '\n\n⚠️ Note: Backend connection failed. This is a simulated response.', 
        sender: 'bot' 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chatbot-container" style={{ 
      maxWidth: '600px', 
      margin: '0 auto', 
      border: '1px solid #ddd', 
      borderRadius: '8px',
      overflow: 'hidden'
    }}>
      {connectionError && (
        <div style={{ 
          backgroundColor: '#fff3cd', 
          color: '#856404', 
          padding: '10px', 
          textAlign: 'center',
          borderBottom: '1px solid #ddd'
        }}>
          ⚠️ Backend connection failed. Running in demo mode with simulated responses.
        </div>
      )}
      <div className="chat-messages" style={{ 
        height: '400px', 
        overflowY: 'auto', 
        padding: '20px',
        backgroundColor: '#f9f9f9'
      }}>
        {messages.map((message, index) => (
          <div 
            key={index} 
            style={{ 
              marginBottom: '10px',
              textAlign: message.sender === 'user' ? 'right' : 'left'
            }}
          >
            <div style={{ 
              display: 'inline-block',
              padding: '10px 15px',
              borderRadius: '18px',
              backgroundColor: message.sender === 'user' ? '#007bff' : '#e9ecef',
              color: message.sender === 'user' ? 'white' : 'black',
              maxWidth: '70%',
              whiteSpace: 'pre-line'
            }}>
              {message.text}
            </div>
          </div>
        ))}
        {isLoading && (
          <div style={{ textAlign: 'left', marginBottom: '10px' }}>
            <div style={{ 
              display: 'inline-block',
              padding: '10px 15px',
              borderRadius: '18px',
              backgroundColor: '#e9ecef',
              color: 'black',
              maxWidth: '70%'
            }}>
              Typing...
            </div>
          </div>
        )}
      </div>
      <form onSubmit={handleSendMessage} style={{ 
        display: 'flex', 
        padding: '10px',
        borderTop: '1px solid #ddd'
      }}>
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Type your message..."
          style={{ 
            flex: 1, 
            padding: '10px', 
            borderRadius: '4px',
            border: '1px solid #ddd',
            marginRight: '10px'
          }}
        />
        <button 
          type="submit"
          style={{ 
            padding: '10px 20px', 
            backgroundColor: '#007bff', 
            color: 'white', 
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Send
        </button>
      </form>

      {/* Payment Button - Only show when there's a payment prompt */}
      {messages.length > 0 && 
       messages.filter(m => m.sender === 'bot').pop()?.text.includes('Pay with Paystack') && (
        <div style={{
          padding: '10px',
          borderTop: '1px solid #ddd',
          textAlign: 'center'
        }}>
          <button
            onClick={() => {
              const lastBotMessage = messages.filter(m => m.sender === 'bot').pop();
              const orderIdMatch = lastBotMessage.text.match(/Order #(\d+)/);
              const amountMatch = lastBotMessage.text.match(/Total: \$(\d+(\.\d{2})?)/);

              if (orderIdMatch && orderIdMatch[1] && amountMatch && amountMatch[1]) {
                const orderId = orderIdMatch[1];
                const amount = parseFloat(amountMatch[1]);
                handlePayment(orderId, amount);
              } else {
                setMessages(prev => [...prev, { 
                  text: 'Sorry, there was an error processing your payment. Please try again.', 
                  sender: 'bot' 
                }]);
              }
            }}
            style={{
              padding: '10px 20px',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px',
              fontWeight: 'bold'
            }}
          >
            Pay with Paystack
          </button>
        </div>
      )}
    </div>
  );
}

export default ChatBot;
