import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import PaystackPop from '@paystack/inline-js';

function PaymentPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [orderDetails, setOrderDetails] = useState(null);

  useEffect(() => {
    // Get order details from URL parameters
    const params = new URLSearchParams(location.search);
    const orderId = params.get('orderId');
    const amount = params.get('amount');
    const deviceId = params.get('deviceId');

    if (!orderId || !amount || !deviceId) {
      setError('Missing payment information. Please return to the chatbot and try again.');
      setLoading(false);
      return;
    }

    setOrderDetails({
      orderId,
      amount: parseFloat(amount),
      deviceId
    });
    setLoading(false);

    // Initialize payment
    initializePayment(orderId, parseFloat(amount), deviceId);
  }, [location]);

  const initializePayment = async (orderId, amount, deviceId) => {
    try {
      // Initialize payment with backend
      const response = await axios.post('http://localhost:3001/api/payment/initialize', 
        { 
          orderId,
          amount: amount * 100, // Convert to kobo
          email: 'customer@example.com'
        },
        { headers: { deviceId } }
      );

      if (response.data.authorization_url) {
        // Open Paystack popup
        const handler = new PaystackPop();
        handler.newTransaction({
          key: process.env.REACT_APP_PAYSTACK_PUBLIC_KEY,
          email: 'customer@example.com',
          amount: amount * 100,
          ref: `ORDER_${orderId}`,
          callback: (response) => {
            // Handle successful payment
            verifyPayment(response.reference, orderId, deviceId);
          },
          onClose: () => {
            // Return to chatbot with payment cancelled message
            navigate(`/?payment=cancelled&orderId=${orderId}`);
          }
        });
      } else {
        setError('Failed to initialize payment. Please try again.');
      }
    } catch (error) {
      console.error('Error initializing payment:', error);
      setError('Failed to initialize payment. Please try again.');
    }
  };

  const verifyPayment = async (reference, orderId, deviceId) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/payment/verify?reference=${reference}`);
      
      if (response.data.status === 'success') {
        // Return to chatbot with success message
        navigate(`/?payment=success&orderId=${orderId}`);
      } else {
        // Return to chatbot with failure message
        navigate(`/?payment=failed&orderId=${orderId}`);
      }
    } catch (error) {
      console.error('Error verifying payment:', error);
      // Return to chatbot with error message
      navigate(`/?payment=error&orderId=${orderId}`);
    }
  };

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        flexDirection: 'column'
      }}>
        <h2>Initializing payment...</h2>
        <p>Please wait while we set up your payment.</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        flexDirection: 'column',
        padding: '20px',
        textAlign: 'center'
      }}>
        <h2>Payment Error</h2>
        <p>{error}</p>
        <button 
          onClick={() => navigate('/')}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Return to Chatbot
        </button>
      </div>
    );
  }

  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      flexDirection: 'column'
    }}>
      <h2>Processing Payment</h2>
      <p>Order #{orderDetails?.orderId} - ${orderDetails?.amount.toFixed(2)}</p>
      <p>Please complete your payment in the popup window.</p>
    </div>
  );
}

export default PaymentPage;
